class Solution {
public:
    vector&lt;int&gt; plusOne(vector&lt;int&gt;& v) {
        int c = 0, l = v.size();

        for (int i = l-1; i &gt;= 0; i--) {
            if (i == l-1) {
                if (v[i] == 9) {
                    v[i] = 0;
                    c = 1;
                } else {
                    v[i] += 1;
                    return v;
                }
            } else {
                if (v[i] == 9) {
                    v[i] = 0;
                    c = 1;
                } else {
                    v[i] += c;
                    return v;
                }
            }
        }

        if (*v.begin() == 0) {
            v.insert(v.begin(), 1);
        }

        return v;
    }
};

