//  Merge_Sorted_Array.cpp
//  LeetCode - C++
//  Created by Bhanu Prasanna

class Solution {
public:
    void merge(vector&lt;int&gt;& nums1, int m, vector&lt;int&gt;& nums2, int n) {
        int i = m - 1;
        int j = n - 1;
        int k = m + n - 1;

        while (j &gt;= 0) {
            if (i &gt;= 0 && nums1[i] &gt; nums2[j]) {
                nums1[k] = nums1[i];
                i--;
            } else {
                nums1[k] = nums2[j];
                j--;
            }
            k--;
        }
    }
};


