class Solution {
public:
    vector&lt;vector&lt;int&gt;&gt; generate(int numRows) {
        vector&lt;vector&lt;int&gt;&gt; v;

        for (int i = 0; i &lt; numRows; i++) {
            vector&lt;int&gt; x(i+1, 1);
            for (int j = 1; j &lt; i; j++) {
                x[j] = v[i-1][j] + v[i-1][j-1];
            }
            v.push_back(x);
        }
        return v;
    }
};


