class Solution {
public:
    vector&lt;int&gt; getRow(int rowIndex) {
        vector&lt;vector&lt;int&gt;&gt; v;
        vector&lt;int&gt; xv;

        for (int i = 0; i &lt;= rowIndex; i++) {
            vector&lt;int&gt; x(i+1, 1);

            for (int j = 1; j &lt; i; j++) {
                x[j] = v[i-1][j] + v[i-1][j-1];
            }
            if (i == rowIndex) {
                return x;
            }

            v.push_back(x);
        }

        return xv;
    }
};


