class Solution {
public:
    int maxProfit(vector&lt;int&gt;& prices) {
        int lsf = INT_MAX;
        int op = 0;
        int pist = 0;

        for (int i = 0; i &lt; prices.size(); i++) {
            if (prices[i] &lt; lsf) {
                lsf = prices[i];
            }
            pist = prices[i] - lsf;
            if (pist &gt; op) {
                op = pist;
            }
        }
        return op;
    }
};


