#include &lt;iostream&gt;
using namespace std;

class Node {
public:
    int data;
    Node* next;

    Node () {
        data = 0;
        next = nullptr;
    }

    Node (int d) {
        data = d;
        next = nullptr;
    }
};

class SLL : public Node {
    Node* head;

public:
    SLL () {
        head = nullptr;
    }

    // Insert Data at the Front of SLL
    void insert_front(int);

    // Insert Data at the Back of SLL
    void insert_back(int);

    // Insert Data in the Middle of SLL
    void insert(int, int);

    // Delete Data from the Front of SLL
    void delete_front();

    // Delete Data from the Back of SLL
    void delete_back();

    // Delete Data from the Middle of SLL
    void del(int);

    // Length of the SLL
    int length();

    // Print SLL
    void printll();

};

/* Delete Functions for Single Linked List */

void SLL::delete_front () {
    Node *t = head;

    if (t == nullptr) {
        cout &lt;&lt; "There are no element in the SLL to delete!" &lt;&lt; endl;
        return;
    }

    head = head-&gt;next;
}

void SLL::delete_back () {
    Node *t = head;

    if (t == nullptr) {
        cout &lt;&lt; "There are no element in the SLL to delete!" &lt;&lt; endl;
        return;
    }

    while (t-&gt;next-&gt;next != nullptr) {
        t = t-&gt;next;
    }
    t-&gt;next = nullptr;
}

void SLL::del(int pos) {
    Node *x = head;
    Node *y = head-&gt;next;

    if (x == nullptr) {
        cout &lt;&lt; "There are no element in the SLL to delete!" &lt;&lt; endl;
        return;
    }

    for (int i = 0; i &lt; pos - 2; i++) {
        x = x-&gt;next;
        y = y-&gt;next;
    }
    x-&gt;next = y-&gt;next;
}

/* Insertion Functions for Single Linked List */

void SLL::insert_front (int d) {
    Node *t = new Node(d);

    if (head == nullptr) {
        head = t;
        return;
    }

    t-&gt;next = head;

    head = t;

}

void SLL::insert_back (int d) {
    Node *t = new Node(d);

    if (head == nullptr) {
        head = t;
        return;
    }

    Node *x = head;

    while (x-&gt;next != nullptr) {
        x = x-&gt;next;
    }
    x-&gt;next = t;
}

void SLL::insert (int d, int pos) {

    if (pos &gt; SLL::length()) {
        SLL::insert_back(d);
        return;
    }

    if (pos == 1) {
        SLL::insert_front(d);
        return;
    }

    Node *t = new Node(d);
    Node *x = head;
    Node *y = head-&gt;next;

    for (int i = 0; i &lt; pos - 2; i++) {
        x = x-&gt;next;
        y = y-&gt;next;
    }

    t-&gt;next = y;
    x-&gt;next = t;
}

/* Length of Single Linked List */

int SLL::length () {
    Node *t = head;
    int c = 0;

    while (t != nullptr) {
        t = t-&gt;next;
        c++;
    }

    return c;
}

/* Print Single Linked List */

void SLL::printll () {
    Node *t = head;

    if (t == nullptr) {
        cout &lt;&lt; "Linked List is Empty" &lt;&lt; endl;
        return;
    }

    while (t != nullptr) {
        if (t-&gt;next == nullptr) {
            cout &lt;&lt; t-&gt;data &lt;&lt; endl;
        } else {
            cout &lt;&lt; t-&gt;data &lt;&lt; " -&gt; ";
        }
        t = t-&gt;next;
    }
}

int main () {
    SLL l;

    l.insert_front(10);
    l.insert_back(20);
    l.insert_back(40);
    l.insert_back(50);
    l.insert_back(60);

    l.insert(30, 3);

    l.insert(30,3);

    l.del(3);

    l.delete_back();
    l.delete_front();

    cout &lt;&lt; "Elements of Linked List are:" &lt;&lt; endl;

    l.printll();

    cout &lt;&lt; endl &lt;&lt; "Count: " &lt;&lt; l.length() &lt;&lt; endl;

    return 0;
}

