/* Delete Functions for Circular Linked List */

void CLL::delete_front() {
    Node *t = head;

    if (t == nullptr) {
        cout &lt;&lt; "There are no element in the CLL to delete!" &lt;&lt; endl;
    }

    head = head-&gt;next;
    last-&gt;next = head;
}

void CLL::delete_back() {
    Node *t = head;

    if (t == nullptr) {
        cout &lt;&lt; "There are no element in the CLL to delete!" &lt;&lt; endl;
    }

    if (t-&gt;next == head) {
        head = nullptr;
        last = nullptr;
    }

    while (t-&gt;next-&gt;next != head) {
        t = t-&gt;next;
    }

    last = t;

    last-&gt;next = head;
}

void CLL::del(int pos) {
    Node *x = head;
    Node *y = head-&gt;next;

    if (x == nullptr) {
        cout &lt;&lt; "There are no element in the CLL to delete!" &lt;&lt; endl;
    }

    if (pos == CLL::length()) {
        CLL::delete_back();
    }

    for (int i = 0; i &lt; pos - 2; i++) {
        x = x-&gt;next;
        y = y-&gt;next;
    }

    x-&gt;next = y-&gt;next;
}

/* Insertion Functions for Circular Linked List */

void CLL::insert_front(int d) {
    Node *t = new Node(d);

    if (head == nullptr) {
        head = t;
        last = t;
        last-&gt;next = head;
        return;
    }

    t-&gt;next = head;
    head = t;
    last-&gt;next = head;
}

void CLL::insert_back (int d) {
    Node *t = new Node(d);

    if (head == nullptr) {
        insert_front(d);
        return;
    }

    t-&gt;next = head;
    last-&gt;next = t;
    last = t;
