        CLL::insert_back(d);
        return;
    }

    if (pos == 1) {
        CLL::insert_front(d);
        return;
    }

    Node *t = new Node(d);
    Node *x = head;
    Node *y = head-&gt;next;

    for (int i = 0; i &lt; pos - 2; i++) {
        x = x-&gt;next;
        y = y-&gt;next;
    }

    t-&gt;next = y;
    x-&gt;next = t;
}

