    Node () {
        data = 0;
        next = nullptr;
    }

    Node (int d) {
        data = d;
        next = nullptr;
    }
};

class Stack : public Node {
    Node* head;
    int MAX;

public:

    Stack (int m) {
        head = nullptr;
        MAX = m;
    }

    // Push an element to the Stack
    void push (int d);

    // Pop an element from the Stack
    int pop ();

    // Check if Stack is Empty
    bool isEmpty ();

    // Check if Stack is Full
    bool isFull ();

    // Get the Top element from the Stack
    int peek ();

    // Print the Stack
    void print ();
};

/* Push an element to the Stack */
void Stack::push (int d) {
    if (Stack::isFull()) {
        cout &lt;&lt; "The Stack is Full. " &lt;&lt; d &lt;&lt; " element cannot be Inserted." &lt;&lt; endl;
        return;
    }

    Node *t = new Node(d);

    if (head == nullptr) {
        t-&gt;next = nullptr;
        head = t;
        return;
    }

    t-&gt;next = head;
    head = t;
}

/* Pop an element from the Stack */
int Stack::pop () {
    if (Stack::isEmpty()) {
        cout &lt;&lt; "The Stack is Empty. No Element is Present." &lt;&lt; endl;
        return -1;
    }

    Node *t = head;

    head = head-&gt;next;

    return t-&gt;data;
}

/* Check if the Stack is Empty */
bool Stack::isEmpty () {
    Node *t = head;

    if (head == nullptr) {
        return true;
    }

    return false;
}

/* Check if the Stack is Full */
bool Stack::isFull () {
    Node *t = head;
    int c = 0;

    while (t != nullptr) {
        c++;
        t = t-&gt;next;
    }

    if (c == MAX) {
        return true;
    }

    return false;
}

/* Get the Top element from the Stack */
int Stack::peek () {
    Node *t = head;

    return t-&gt;data;
}

/* Print the Stack */
void Stack::print () {
    Node *t = head;

    while (t != nullptr) {
        if (t-&gt;next == nullptr) {
            cout &lt;&lt; t-&gt;data &lt;&lt; endl;
        }
        else {
            cout &lt;&lt; t-&gt;data &lt;&lt; " -&gt; ";
        }
        t = t-&gt;next;
    }
}

