/* Reverse a SLL using Iteration */
void SLL::rev_iter () {
    Node *cur = head, *prev = nullptr;

    while (cur != nullptr) {
        Node *t = cur-&gt;next;
        cur-&gt;next = prev;
        prev = cur;
        cur = t;
    }
    head = prev;
}

